CREATE OR REPLACE PROCEDURE rep_gerar_data_prevista_alta ( cd_estabelecimento_p bigint, nr_atendimento_p bigint, dt_previsto_alta_p timestamp, ie_probabilidade_alta_p text, ds_obs_prev_alta_p text, nm_usuario_p text) AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(10);
nr_seq_evento_w			bigint;
qt_idade_w			bigint;
cd_profissional_w		varchar(10);
						
						 
c04 CURSOR FOR 
	SELECT	a.nr_seq_evento 
	from	regra_envio_sms a 
	where	a.cd_estabelecimento	= cd_estabelecimento_p 
	and		a.ie_evento_disp	= 'ALP' 
	and	((ie_probabilidade_alta = ie_probabilidade_alta_p) or (coalesce(ie_probabilidade_alta::text, '') = '')) 
	and	qt_idade_w between coalesce(qt_idade_min,0)	and coalesce(qt_idade_max,9999)	 
	and	coalesce(a.ie_situacao,'A') = 'A';
			

BEGIN 
 
update 	atendimento_paciente 
set	dt_previsto_alta = CASE WHEN dt_previsto_alta_p = NULL THEN dt_previsto_alta  ELSE dt_previsto_alta_p END , 
	DT_VER_PREV_ALTA = clock_timestamp(), 
	ie_probabilidade_alta = CASE WHEN ie_probabilidade_alta_p = NULL THEN  ie_probabilidade_alta  ELSE ie_probabilidade_alta_p END , 
	ds_obs_prev_alta = substr(CASE WHEN coalesce(ds_obs_prev_alta_p,'')='' THEN ds_obs_prev_alta  ELSE ds_obs_prev_alta||chr(13)||chr(10)||wheb_mensagem_pck.get_texto(455976)||PKG_DATE_FORMATERS.to_varchar(clock_timestamp(), 'timestamp', cd_estabelecimento_p, nm_usuario_p)||chr(13)||chr(10)||ds_obs_prev_alta_p END ,1,255) 
where 	nr_atendimento = nr_atendimento_p;
 
 
select	max(cd_pessoa_fisica) 
into STRICT 	cd_pessoa_fisica_w 
from 	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
qt_idade_w	:= coalesce(obter_idade_pf(cd_pessoa_fisica_w,clock_timestamp(),'A'),0);
 
open c04;
	loop 
	fetch c04 into	 
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on c04 */
		begin 
		CALL gerar_evento_paciente_trigger(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_w,null,nm_usuario_p);
		end;
	end loop;
	close c04;
	 
	 
commit;
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_profissional_w 
from	usuario 
where	nm_usuario = nm_usuario_p;
 
CALL gerar_atend_previsao_alta(nr_atendimento_p,cd_profissional_w,dt_previsto_alta_p,ie_probabilidade_alta_p,nm_usuario_p,ds_obs_prev_alta_p);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
