CREATE OR REPLACE PROCEDURE rep_jejum_afterpost_del_js ( nr_prescricao_p bigint, nm_usuario_p text, cd_perfil_p bigint ) AS $body$
DECLARE

				 
 
ie_prescr_dieta_w	varchar(10) := '';

				 

BEGIN 
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
	begin 
 
	select	CASE WHEN sum(nr)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_prescr_dieta_w	 
	from ( 	SELECT 1 nr 
			from  prescr_dieta 
			where  nr_prescricao = nr_prescricao_p 
			
union
 
			SELECT 1 nr 
			from  prescr_material 
			where  nr_prescricao = nr_prescricao_p 
			and   ie_agrupador  in (8,12,16)) alias3;
 
			 
	if (ie_prescr_dieta_w = 'S') then 
		begin 
		 
		CALL plt_gerar_horarios_sem_lib(nr_prescricao_p,'N',nm_usuario_p);
 
		 
		end;
	end if;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
