CREATE OR REPLACE PROCEDURE rep_jejum_afterpost_js ( nr_acao_executada_p bigint, nr_prescricao_p bigint, nr_atendimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nm_usuario_p text, cd_perfil_p bigint, ie_dieta_vig_p INOUT text, ds_pergunta_p INOUT text ) AS $body$
DECLARE

				 
ie_acao_w	varchar(1) := '';
ie_dieta_vig_w	varchar(10) := '';
ds_pergunta_w	varchar(255) := '';
				

BEGIN 
 
if (nr_acao_executada_p = 2) then 
	begin 
	ie_acao_w := 'A';
	end;
else 
	begin 
	ie_acao_w := 'E';
	end;
end if;
 
 
CALL atualiza_horario_nut_jejum(nr_prescricao_p,ie_acao_w,nm_usuario_p,cd_perfil_p);
 
CALL atualizar_dose_esp_dieta(nr_prescricao_p,cd_perfil_p,nm_usuario_p);
 
 
ie_dieta_vig_w	:= obter_se_dieta_vig(nr_atendimento_p,dt_inicio_p,dt_fim_p,nm_usuario_p);
 
ds_pergunta_w	:= substr(obter_texto_tasy(93817, wheb_usuario_pck.get_nr_seq_idioma),1,255);
 
 
ie_dieta_vig_p	:= ie_dieta_vig_w;
ds_pergunta_p	:= ds_pergunta_w;
 
end;
