SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rep_medicamentos_affter_post ( nr_prescricao_p bigint, nr_sequencia_p bigint, gerar_reconstituicao_p text, ajustar_prescr_material_p text, consistir_item_p text, ie_chamar_diluicao_p text, nm_usuario_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, ds_dias_p bigint, consistir_alerta_mat_p INOUT text, ie_obriga_diluicao_p INOUT text, ds_erro_p INOUT text, ie_se_necessario_p text) AS $body$
DECLARE

 
ds_erro_w				varchar(2000);	
consistir_alerta_mat_w	varchar(2000);
ie_obriga_diluicao_w	varchar(1) := 'N';
ie_diluicao_w			varchar(1);
cd_setor_atendimento_w	integer;
cd_prescritor_w			varchar(10);
cd_material_w			integer;
ie_via_aplicacao_w		varchar(5);
vl_parametro_w			varchar(255);
nr_atendimento_w		varchar(10);
cd_intervalo_w			varchar(7);
ie_bomba_infusao_w		prescr_material.ie_bomba_infusao%type;
	

BEGIN 
 
if (ds_dias_p IS NOT NULL AND ds_dias_p::text <> '') then 
 
  update  prescr_material 
  set   qt_dias_tratamento = ds_dias_p 
  where  nr_prescricao   = nr_prescricao_p 
  and   nr_sequencia    = nr_sequencia_p;
   
end if;
 
if ( 'S' = gerar_reconstituicao_p)	then 
	CALL gerar_reconst_diluicao(nr_prescricao_p, nr_sequencia_p, 'A');
end if;
 
if ( 'S' = ajustar_prescr_material_p)	then 
	CALL ajustar_prescr_material(nr_prescricao_p, nr_sequencia_p);
end if;
 
if ( 'S' = consistir_item_p)	then 
	CALL atualiza_antimicrobiano_dia(nr_prescricao_p, nr_sequencia_p);
	ds_erro_w := consistir_prescr_material(nr_prescricao_p, nr_sequencia_p, nm_usuario_p, cd_perfil_p, ds_erro_w);
	consistir_alerta_mat_w := consistir_alerta_prescr_mat(nr_prescricao_p, nr_sequencia_p, consistir_alerta_mat_w);
end if;
 
vl_parametro_w := Obter_Param_Usuario(924, 600, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);
 
if (vl_parametro_w = 'S') then 
	CALL calcular_vol_dil_afterpost(nr_prescricao_p, nm_usuario_p);
end if;
 
if ('S' = ie_chamar_diluicao_p) then 
	begin 
	select 	coalesce(max('S'),'N') 
	into STRICT	ie_diluicao_w 
	from 	prescr_material 
	where 	nr_sequencia_diluicao 	= nr_sequencia_p 
	and 	nr_prescricao 		= nr_prescricao_p 
	and 	qt_dose > 0;
	 
	if ('N' = ie_diluicao_w) then 
		begin 
		select	cd_setor_atendimento, 
				cd_prescritor, 
				nr_atendimento 
		into STRICT	cd_setor_atendimento_w, 
				cd_prescritor_w, 
				nr_atendimento_w 
		from	prescr_medica 
		where 	nr_prescricao	= nr_prescricao_p;
 
		select 	max(cd_material), 
				max(ie_via_aplicacao), 
				max(cd_intervalo), 
				max(ie_bomba_infusao) 
		into STRICT	cd_material_w, 
				ie_via_aplicacao_w, 
				cd_intervalo_w, 
				ie_bomba_infusao_w 
		from	prescr_material 
		where	nr_sequencia 	= nr_sequencia_p 
		and		nr_prescricao = nr_prescricao_p;
		 
		ie_obriga_diluicao_w := Obter_Padrao_Param_Prescr(nr_atendimento_w, cd_material_w, ie_via_aplicacao_w, cd_setor_atendimento_w, cd_prescritor_w, null, null, ie_se_necessario_p, 'L', cd_intervalo_w);
		end;
	end if;
	end;
end if;
 
ie_obriga_diluicao_p	:= ie_obriga_diluicao_w;
ds_erro_p				:= ds_erro_w;
consistir_alerta_mat_p	:= consistir_alerta_mat_w;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
