CREATE OR REPLACE PROCEDURE rep_prescr_solucao_affter_post ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, ie_esquema_alternado_p text, nm_usuario_p text) AS $body$
DECLARE

 
qtd_w	bigint;


BEGIN 
 
CALL reordenar_solucoes(nr_prescricao_p);
 
--verificar se possui registros 
select 	count(*) 
into STRICT	qtd_w 
from	prescr_solucao 
where 	nr_prescricao	= nr_prescricao_p 
and	nr_seq_solucao	= nr_seq_solucao_p;
 
if ( qtd_w > 0)	then 
	CALL ajustar_prescr_mat_solucao(nr_prescricao_p, nr_seq_solucao_p);
end if;
 
if ('S' = ie_esquema_alternado_p)	then 
	CALL calcular_etapas_esquema(nr_prescricao_p, nr_seq_solucao_p);
end if;
       
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
