SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rep_remover_horarios ( nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_material_w		prescr_material.nr_sequencia%type;
nr_seq_solucao_w		prescr_solucao.nr_seq_solucao%type;
nr_seq_procedimento_w	prescr_procedimento.nr_sequencia%type;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	prescr_material a
where	a.nr_prescricao	= nr_prescricao_p;

c02 CURSOR FOR
SELECT	a.nr_seq_solucao
from	prescr_solucao a
where	a.nr_prescricao	= nr_prescricao_p;

c03 CURSOR FOR
SELECT	a.nr_sequencia
from	prescr_procedimento a
where	a.nr_prescricao	 = nr_prescricao_p;


BEGIN

delete from prescr_mat_hor where nr_prescricao = nr_prescricao_p;
delete from prescr_proc_hor where nr_prescricao = nr_prescricao_p;

open c01;
loop
fetch c01 into
	nr_seq_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update	prescr_material
	set	ds_horarios  = NULL,
		hr_prim_horario  = NULL,
		cd_intervalo  = NULL
	where	nr_sequencia = nr_seq_material_w
	and	nr_prescricao = nr_prescricao_p;

end loop;
close c01;

open c02;
loop
fetch c02 into
	nr_seq_solucao_w;
EXIT WHEN NOT FOUND; /* apply on c02 */

	update	prescr_solucao
	set	ds_horarios  = NULL,
		ds_horarios_medico  = NULL,
		dt_prim_horario  = NULL,
		hr_prim_horario  = NULL,
		cd_intervalo  = NULL,
		nr_etapas = 0
	where	nr_seq_solucao = nr_seq_solucao_w
	and	nr_prescricao = nr_prescricao_p;

end loop;
close c02;

open c03;
loop
fetch c03 into
	nr_seq_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on c03 */

	update	prescr_procedimento
	set	ds_horarios  = NULL,
		cd_intervalo  = NULL,
		dt_prev_execucao  = NULL
	where	nr_sequencia = nr_seq_procedimento_w
	and	nr_prescricao = nr_prescricao_p;

end loop;
close c03;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
	commit;
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
