CREATE OR REPLACE PROCEDURE rep_suspender_horarios_duplos ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_motivo_p bigint, nr_atendimento_p bigint, cd_material_p bigint, nr_seq_item_p bigint, nr_seq_horario_p bigint, nr_alteracao_p bigint, dt_horario_p timestamp, ie_tipo_item_p text, ds_motivo_p text, nm_usuario_p text) AS $body$
DECLARE


ds_erro_w	varchar(255);

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then	
	begin
	ds_erro_w := consiste_susp_prescr_mat_hor(
		nr_prescricao_p, nr_sequencia_p, wheb_usuario_pck.get_cd_estabelecimento, nm_usuario_p, ds_erro_w);
	
	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
		begin
		--ds_erro_w 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(214150,'ERRO='||ds_erro_w);	
		end;
	end if;
	
	CALL suspender_prescr_mat_hor(nr_sequencia_p,nm_usuario_p,nr_seq_motivo_p,ds_motivo_p,null,null);

	CALL gerar_prescr_mat_hor_compl(nr_seq_horario_p, nm_usuario_p, 'S');

    	CALL atualiza_ie_horario_susp(nr_prescricao_p, nr_seq_item_p, 'PRESCR_MATERIAL');

	CALL gerar_alter_hor_prescr_adep(
		nr_atendimento_p,
		ie_tipo_item_p,
		cd_material_p,
		nr_prescricao_p,
		nr_seq_item_p,
		nr_seq_horario_p,
		dt_horario_p,
		12,
		null,
		null,
		nr_seq_motivo_p,
		nm_usuario_p);
	end;
end if;
commit;
end;
$body$
