SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rep_suspender_jejum_vigente ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_prescricao_w		bigint;
					

BEGIN 
 
select	coalesce(max(a.nr_prescricao),0) 
into STRICT	nr_prescricao_w 
from	rep_jejum b, 
	prescr_medica a 
where	a.nr_prescricao	= b.nr_prescricao 
and	b.nr_sequencia	= nr_sequencia_p 
and	plt_obter_se_liberado(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia) = 'N';
 
update	rep_jejum 
set	ie_suspenso = 'S', 
	nm_usuario	= nm_usuario_p, 
	dt_atualizacao	= clock_timestamp() 
where	nr_sequencia = nr_sequencia_p;
 
commit;
 
if (nr_prescricao_w	> 0) then 
	CALL PLT_gerar_horarios_sem_lib(nr_prescricao_w, 'N', nm_usuario_p);
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
