SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE request_remand_nur_sum (nr_sequencia_P bigint, ds_comment_p text) AS $body$
DECLARE


nr_seq_sum_enf_w    sumario_enfermagem.nr_sequencia%type;
dt_aprovacao_w      sumario_enf_conf_list.dt_aprovacao%type;


BEGIN

select nr_seq_sum_enf,
      dt_aprovacao
into STRICT nr_seq_sum_enf_w,
     dt_aprovacao_w
from sumario_enf_conf_list
where nr_sequencia = nr_sequencia_p;

if (nr_seq_sum_enf_w > 0) then

  if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
    update sumario_enf_conf_list
    set ie_remand = 'S',
        dt_aprovacao  = NULL,
        nm_aprovador  = NULL,
        ds_remand = ds_comment_p
    where nr_sequencia = nr_sequencia_p;
  else 
    update sumario_enf_conf_list
    set ie_remand = 'S',
        ds_remand = ds_comment_p
    where nr_sequencia = nr_sequencia_p;
  end if;

  update sumario_enfermagem
  set dt_liberacao  = NULL,
      ie_remand = 'S',
      ds_remand = ds_comment_p,
      DT_APPROVED  = NULL
  where nr_sequencia = nr_seq_sum_enf_w;

  commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
