CREATE OR REPLACE PROCEDURE req_consiste_item_outra_aprov ( nr_requisicao_p bigint, cd_processo_aprov_p text) AS $body$
DECLARE


qt_itens_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_itens_w
from	item_requisicao_material
where	nr_requisicao = nr_requisicao_p;

if (qt_itens_w > 0) then
	select	count(*)
	into STRICT	qt_itens_w
	from	item_requisicao_material
	where	nr_requisicao = nr_requisicao_p
	and	coalesce(cd_processo_aprov,'0') <> coalesce(cd_processo_aprov_p,'0');

	if (qt_itens_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266268);
		--'Não é permitido inserir este item, pois ele irá cair em um processo de aprovação diferente dos demais itens desta  requisição. ' ||
		--'Verifique o parâmetro [90].');
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
