CREATE OR REPLACE PROCEDURE reservar_horario_agecons ( nr_seq_agenda_p bigint, nm_usuario_p text, ie_reservado_p INOUT text) AS $body$
DECLARE


qt_agenda_w		bigint;
ie_reservado_w	varchar(1) := 'S';
cd_agenda_w		bigint;
dt_agenda_w		timestamp;
i				smallint;


BEGIN
/* acesso exclusivo tabela */

--lock table agenda_consulta in exclusive mode;
begin

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	/*select 	max(cd_agenda),
		max(dt_agenda)
	into	cd_agenda_w,
		dt_agenda_w
	from	agenda_consulta
	where	nr_sequencia		= nr_seq_agenda_p;*/
	/*
	select	1
	into	i
	from	agenda_consulta
	where	cd_agenda = cd_agenda_w
	and	dt_agenda = dt_agenda_w
	--and	dt_agenda between trunc(dt_agenda_w) and trunc(dt_agenda_w) + 86399/86400
	for update;
	*/
	select	1
	into STRICT	i
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p
	for update;

	select	coalesce(count(*),0)
	into STRICT	qt_agenda_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p
	and	coalesce(ie_status_agenda,'L')	in ('L','LF');

	if (qt_agenda_w > 0) then
		update	agenda_consulta
		set		ie_status_agenda	= 'N',
				nm_usuario			= nm_usuario_p,
				nm_usuario_acesso	= nm_usuario_p
		where	nr_sequencia		= nr_seq_agenda_p;
		commit;
		ie_reservado_w := 'S';
	else
		begin
		select	coalesce(count(*),0)
		into STRICT	qt_agenda_w
		from	agenda_consulta
		where	nr_sequencia		= nr_seq_agenda_p
		and		nm_usuario_acesso 	= nm_usuario_p
		and		(nm_paciente IS NOT NULL AND nm_paciente::text <> '');

		if (qt_agenda_w > 0) then
			ie_reservado_w := 'S';
		else
			ie_reservado_w := 'N';
		end if;
		end;
	end if;
end if;
exception
	when others then
	ie_reservado_w := 'H';
	commit;
end;

commit;

ie_reservado_p := ie_reservado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
