SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reservar_horario_agenda_cons (nr_seq_agenda_p bigint, nm_usuario_p text, ie_reservado_p INOUT text) AS $body$
DECLARE


qt_agenda_w		bigint;
ie_reservado_w	varchar(1) := 'S';
cd_agenda_w		bigint;
i			smallint;
dt_agenda_w	timestamp;


BEGIN

begin

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	select 	max(cd_agenda),
		max(dt_agenda)
	into STRICT	cd_agenda_w,
		dt_agenda_w
	from	agenda_consulta
	where	nr_sequencia		= nr_seq_agenda_p;

	/* acesso exclusivo tabela */

	select	1
	into STRICT	i
	from	agenda_consulta
	where	cd_agenda = cd_agenda_w
	and	trunc(dt_agenda) = trunc(dt_agenda_w)
	for update;

	--lock table agenda_consulta in exclusive mode;
	select	coalesce(count(*),0)
	into STRICT	qt_agenda_w
	from	agenda_consulta
	where	nr_sequencia			= nr_seq_agenda_p
	and	coalesce(ie_status_agenda,'L')	in ('L','LF');

	if (qt_agenda_w > 0) then
		update	agenda_consulta
		set	ie_status_agenda	= CASE WHEN ie_status_agenda='L' THEN 'N'  ELSE 'LF' END ,
			nm_usuario		= nm_usuario_p,
			nm_usuario_acesso	= nm_usuario_p
		where	nr_sequencia		= nr_seq_agenda_p;
		commit;
		ie_reservado_w := 'S';
	else
		ie_reservado_w := 'N';
	end if;
end if;
exception
	when others then
	commit;
end;

commit;

ie_reservado_p := ie_reservado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
