SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reservar_horario_livre_ag_serv (nr_seq_agenda_p bigint) AS $body$
DECLARE


qt_agenda_w	bigint;
cd_agenda_w	bigint;
i		smallint;
dt_agenda_w	timestamp;


BEGIN

begin
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	select 	max(cd_agenda),
		max(dt_agenda)
	into STRICT	cd_agenda_w,
		dt_agenda_w
	from	agenda_consulta
	where	nr_sequencia		= nr_seq_agenda_p;

	/* acesso exclusivo tabela */

	select	1
	into STRICT	i
	from	agenda_consulta
	where	cd_agenda = cd_agenda_w
	and	trunc(dt_agenda) = trunc(dt_agenda_w)
	for update;

	--lock table agenda_consulta in exclusive mode;A
	select	coalesce(count(*),0)
	into STRICT	qt_agenda_w
	from	agenda_consulta
	where	nr_sequencia 			= nr_seq_agenda_p
	and	coalesce(ie_status_agenda,'L')	= 'L';

	if (qt_agenda_w > 0) then

		update	agenda_consulta
		set	ie_status_agenda	= 'N'
		where	nr_sequencia		= nr_seq_agenda_p;

		commit;

	end if;
end if;
exception
	when others then
	commit;
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE reservar_horario_livre_ag_serv (nr_seq_agenda_p bigint) FROM PUBLIC;

