SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reservar_horario_mult_age (nr_seq_agenda_p bigint, cd_tipo_agenda_p bigint, nm_usuario_p text, ie_reservado_p INOUT text) AS $body$
DECLARE


ie_reservado_w		varchar(1) := 'S';
qt_w				bigint;
qt_agenda_w			bigint;


BEGIN

    begin
        if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (cd_tipo_agenda_p IS NOT NULL AND cd_tipo_agenda_p::text <> '') then

            if (cd_tipo_agenda_p = 2) then
                select    1
                into STRICT    qt_w
                from    agenda_paciente
                where    nr_sequencia = nr_seq_agenda_p
                for update;

                select	count(*)
                into STRICT	qt_agenda_w
                from	agenda_paciente
                where	 nr_sequencia = nr_seq_agenda_p
                and		coalesce(ie_status_agenda,'L')    in ('L','LF');

                if (qt_agenda_w > 0) then
                    update	agenda_paciente
                    set		nm_usuario			= nm_usuario_p,
							nm_usuario_acesso	= nm_usuario_p
                    where	nr_sequencia		= nr_seq_agenda_p;
                    commit;
                    ie_reservado_w := 'S';
                else

                    select	coalesce(max('S'), 'N')
                    into STRICT    ie_reservado_w
                    from    agenda_paciente
                    where	nr_sequencia		= nr_seq_agenda_p
                    and		nm_usuario_acesso	= nm_usuario_p
                    and		(nm_paciente IS NOT NULL AND nm_paciente::text <> '');

                end if;

            else
                select	1
                into STRICT	qt_w
                from	agenda_consulta
                where	nr_sequencia = nr_seq_agenda_p
                for	update;

                select	count(*)
                into STRICT	qt_agenda_w
                from	agenda_consulta
                where	nr_sequencia = nr_seq_agenda_p
                and		coalesce(ie_status_agenda,'L')    in ('L','LF');

                if (qt_agenda_w > 0) then
                    update	agenda_consulta
                    set		nm_usuario			= nm_usuario_p,
							nm_usuario_acesso	= nm_usuario_p
                    where	nr_sequencia		= nr_seq_agenda_p;
                    commit;
                    ie_reservado_w := 'S';
                else
                    select	coalesce(max('S'), 'N')
                    into STRICT    ie_reservado_w
                    from    agenda_consulta
                    where	nr_sequencia		= nr_seq_agenda_p
                    and		nm_usuario_acesso	= nm_usuario_p
                    and		(nm_paciente IS NOT NULL AND nm_paciente::text <> '');
                end if;
            end if;
        end if;

    exception
    when others then
        ie_reservado_w := 'H';
    end;

ie_reservado_p := ie_reservado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
