CREATE OR REPLACE PROCEDURE reservar_leito_paciente (cd_pessoa_fisica_p text, cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nm_usuario_p text) AS $body$
