CREATE OR REPLACE PROCEDURE reserv_hor_dur_age_exame (nr_seq_agenda_p bigint, nm_usuario_p text, nr_minuto_duracao_p bigint, ie_reservado_p INOUT text) AS $body$
DECLARE


cd_agenda_w				bigint;
dt_agenda_w				timestamp;
nr_minuto_duracao_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	agenda_paciente
	where	1 = 1
	and		hr_inicio between dt_agenda_w and dt_agenda_w + ((nr_minuto_duracao_p -1 ) / 1440)
	and		cd_agenda = cd_agenda_w
	and		nr_sequencia <> nr_seq_agenda_p
	-- Restrição igual a rotina ageint_reserv_hor_agenda_exame
	and	(coalesce(ie_status_agenda,'L')	= 'L'
	or	((ie_status_agenda = 'B')
	and (coalesce(dt_bloqueio::text, '') = '')))
	;

BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	select	max(cd_agenda),
			max(hr_inicio),
			max(nr_minuto_duracao)
	into STRICT	cd_agenda_w,
			dt_agenda_w,
			nr_minuto_duracao_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	if (nr_minuto_duracao_p > nr_minuto_duracao_w) then
		for r_c01 in c01 loop
			begin
			ie_reservado_p := ageint_reserv_hor_agenda_exame(r_c01.nr_sequencia, nm_usuario_p, ie_reservado_p);
			end;
		end loop;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
