CREATE OR REPLACE PROCEDURE reset_rp_medicine_seq (CD_PESSOA_FISICA_P text, IE_IDENTITY_CODE_P text ) AS $body$
DECLARE

NR_SEQ_MAX_W bigint := 0;


BEGIN

		EXECUTE 'drop sequence RP_MEDICINE_SEQ';
		IF (IE_IDENTITY_CODE_P = 'S') THEN	
				EXECUTE 'create sequence RP_MEDICINE_SEQ increment by 1 start with 1 maxvalue 9999999999 cycle cache 10';
		ELSE
				select max(NR_SEQUENCIA) into STRICT NR_SEQ_MAX_W from RP_MEDICINE where cd_pessoa_fisica = CD_PESSOA_FISICA_P;
				EXECUTE 'create sequence RP_MEDICINE_SEQ increment by 1 start with ' || (NR_SEQ_MAX_W + 1) || ' maxvalue 9999999999 cycle cache 10';
		END IF;
		commit;
			
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
