SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE restaurar_horario_aut_agendas (qt_minutos_p bigint) AS $body$
DECLARE



nr_sequencia_w	bigint;
cd_agenda_w		bigint;
ds_erro_w		varchar(255);


--Agenda de Consultas e Servicos
C01 CURSOR FOR
	SELECT	b.cd_agenda,
			a.nr_sequencia			
	from	agenda_consulta a,
			agenda b
	where	a.cd_agenda			= b.cd_agenda
	and		b.cd_tipo_agenda		in (3,5)
	and		b.ie_situacao		= 'A'
	and		(a.nm_usuario_acesso IS NOT NULL AND a.nm_usuario_acesso::text <> '')
	and		a.ie_status_agenda in ('N','M')
	and		a.dt_agendamento between(clock_timestamp() - interval '1 days') and (clock_timestamp() - qt_minutos_p/1440 )
	order by 1;
	
--Agenda de Exames
C02 CURSOR FOR
	SELECT	b.cd_agenda,
			a.nr_sequencia
	from	agenda_paciente a,
			agenda b
	where	a.cd_agenda			= b.cd_agenda
	and		b.cd_tipo_agenda	= 2
	and		b.ie_situacao		= 'A'
	and		(a.nm_usuario_acesso IS NOT NULL AND a.nm_usuario_acesso::text <> '')
	and		a.ie_status_agenda in ('N','M')
	and	a.dt_agendamento between(clock_timestamp() - interval '1 days') and (clock_timestamp() - qt_minutos_p/1440 )
	order by 1;
	

BEGIN
if (qt_minutos_p IS NOT NULL AND qt_minutos_p::text <> '')then
	begin
	
	--Agenda de Consultas e Servicos
	open C01;
	loop
	fetch C01 into	
		cd_agenda_w,
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			begin
			ds_erro_w := AgeCons_Restaurar_Horario(cd_agenda_w, nr_sequencia_w, null, wheb_mensagem_pck.get_texto(795975), 'Tasy', ds_erro_w);					
			end;	
		
		end if;
		
		end;
	end loop;
	close C01;

	--Agenda de Exames
	open C02;
	loop
	fetch C02 into	
		cd_agenda_w,
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		
		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			begin
			update	agenda_paciente
			set		ie_status_agenda	= 'L',
					nm_usuario_acesso	= ''
			where	nr_sequencia		= nr_sequencia_w;			
			end;	
		
		end if;
		
		end;
	end loop;
	close C02;
	
	end;
	
commit;

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE restaurar_horario_aut_agendas (qt_minutos_p bigint) FROM PUBLIC;

