CREATE OR REPLACE PROCEDURE resubmit_eclipse_claim ( nr_batch_seq_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, cd_error_string_p INOUT text ) AS $body$
DECLARE


cd_claim_type		varchar(10);
cd_transaction_w 	varchar(25);

cResubmitClaim CURSOR FOR
SELECT 	a.nr_interno_conta
from	convenio_retorno_item a ,
	convenio_retorno b
where	a.nr_seq_retorno =  nr_batch_seq_p;

BEGIN
for	rResubmitClaim in cResubmitClaim loop

	cd_claim_type := get_eclipse_claim_type(rResubmitClaim.nr_interno_conta);
  	if (cd_claim_type = 'DVA') then
		cd_transaction_w := dva_claim_request(nm_usuario_p, cd_estabelecimento_p, null, rResubmitClaim.nr_interno_conta, rResubmitClaim.nr_interno_conta, 'N', 'S', cd_transaction_w);
	end if;
end loop;

end;
