SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE result_select_micro_datainnov ( cd_barras_p text, nm_usuario_aprov_p text) AS $body$
DECLARE



nr_prescricao_w		prescr_medica.nr_prescricao%type;
nr_seq_resultado_w	exame_lab_resultado.nr_seq_resultado%type;
nr_seq_result_item_w	exame_lab_result_item.nr_sequencia%type;
nr_seq_exame_w		exame_laboratorio.nr_Seq_exame%type;
ds_valor_w		exame_lab_valor.ds_valor%type;
ie_existe_antib_w	varchar(1);
nr_seq_prescr_w		bigint;


BEGIN

select 	max(nr_prescricao),
	max(nr_seq_prescr)
into STRICT	nr_prescricao_w,
	nr_seq_prescr_w
from	prescr_proc_mat_item
where	cd_barras = to_char((cd_barras_p)::numeric );

if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then

	select 	max(nr_seq_resultado)
	into STRICT	nr_seq_resultado_w
	from	exame_lab_resultado
	where	nr_prescricao = nr_prescricao_w;

	select	max(i.nr_sequencia)
	into STRICT	nr_seq_result_item_w
	from	exame_lab_result_item i,
		exame_laboratorio e
	where 	i.nr_seq_exame		= e.nr_seq_exame
	and	i.nr_seq_resultado 	= nr_seq_resultado_w
	and	i.nr_seq_prescr		= nr_seq_prescr_w
	and	e.ie_formato_resultado	in ('SM','SDM');

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_antib_w
	from	exame_lab_result_antib a
	where	a.nr_seq_resultado = nr_seq_resultado_w
	and	a.nr_seq_result_item = nr_seq_result_item_w;

	if (ie_existe_antib_w = 'S') then

		select	max(nr_seq_exame)
		into STRICT	nr_seq_exame_w
		from	exame_lab_result_item a
		where	a.nr_seq_resultado = nr_seq_resultado_w
		and	a.nr_sequencia = nr_seq_result_item_w;

		select	max(ds_valor)
		into STRICT	ds_valor_w
		from	exame_lab_valor a
		where	a.nr_seq_exame = nr_seq_exame_w
		and	a.ie_tipo_valor_integr = 1;

		if (ds_valor_w IS NOT NULL AND ds_valor_w::text <> '') then

			update	exame_lab_result_item
			set	ds_resultado = ds_valor_w
			where	nr_seq_resultado = nr_seq_resultado_w
			and	nr_sequencia = nr_seq_result_item_w;

			commit;
		end if;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
