CREATE OR REPLACE PROCEDURE resumo_gastos_devolver_todos (nr_cirurgia_p bigint) AS $body$
DECLARE


cd_material_w		bigint;
ie_origem_gasto_w	smallint;

C01 CURSOR FOR
	SELECT	cd_material, ie_origem_gasto
	from	cirurgia_agente_disp
	where	nr_cirurgia = nr_cirurgia_p
	and	ie_operacao = 'D';



BEGIN
open C01;
loop
fetch C01 into
	cd_material_w,
	ie_origem_gasto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL grava_cirurgia_dispensacao(cd_material_w,
					'',
					1,
					nr_cirurgia_p,
					(wheb_usuario_pck.get_nm_usuario),
					'E',
					'',
					'',
					0,
					null,
					ie_origem_gasto_w,
					null,
					null,
               null);
	end;
end loop;
close C01;

commit;

end;
$body$
