CREATE OR REPLACE PROCEDURE retirar_acompanhante_leito ( cd_unidade_basica_p text, cd_unidade_compl_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_parametro_w	varchar(255);


BEGIN

if (cd_unidade_basica_p IS NOT NULL AND cd_unidade_basica_p::text <> '') and (cd_unidade_compl_p IS NOT NULL AND cd_unidade_compl_p::text <> '') then
	begin
	vl_parametro_w := Obter_Param_Usuario(3111, 54, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);

	if (vl_parametro_w = 'S') then
		update	unidade_atendimento
		set	ie_status_unidade	= 'A',
			nr_atendimento_Acomp	 = NULL,
			cd_paciente_reserva	 = NULL,
			nm_pac_reserva		 = NULL,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	cd_unidade_basica	= cd_unidade_basica_p
		and	cd_unidade_compl	= cd_unidade_compl_p;
	else
		update	unidade_atendimento
		set	ie_status_unidade	= 'L',
			nr_atendimento_Acomp	 = NULL,
			cd_paciente_reserva	 = NULL,
			nm_pac_reserva		 = NULL,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	cd_unidade_basica	= cd_unidade_basica_p
		and	cd_unidade_compl	= cd_unidade_compl_p;
	end if;

	commit;

	end;
end if;

end;
