SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE retirar_dt_integracao_exame (nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_mat_item_w	bigint;
nr_seq_prescr_w		bigint;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
			a.nr_seq_prescr
	from	prescr_proc_mat_item a,
		prescr_procedimento b
	where	a.nr_seq_prescr  = b.nr_sequencia
	and	a.nr_prescricao = b.nr_prescricao
	and 	(b.nr_seq_exame IS NOT NULL AND b.nr_seq_exame::text <> '')
	and	(a.dt_integracao IS NOT NULL AND a.dt_integracao::text <> '')
	and	a.nr_prescricao = nr_prescricao_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_proc_mat_item_w,
	nr_seq_prescr_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update 	prescr_proc_mat_item
	set	dt_integracao  = NULL
	where	nr_sequencia = nr_seq_proc_mat_item_w;

	update	prescr_procedimento
	set		dt_integracao  = NULL
	where	nr_prescricao = nr_prescricao_p
	and		nr_sequencia = nr_seq_prescr_w;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
