SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE retirar_item_pacote (nr_sequencia_p bigint, ie_proc_mat_p bigint, ie_ratear_pacote_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_atendimento_w		bigint;
cd_convenio_w			bigint;
nr_interno_conta_w		bigint;
cd_procedimento_w		bigint;
nr_seq_proc_pacote_w		bigint;
cd_material_w			bigint;
ie_auditoria_w			procedimento_paciente.ie_auditoria%type;


BEGIN 
 
if (ie_proc_mat_p = 1) then 
 
	select	coalesce(max(nr_atendimento),0), 
		coalesce(max(cd_convenio),0), 
		coalesce(max(nr_interno_conta),0), 
		coalesce(max(cd_procedimento),0), 
		coalesce(max(nr_seq_proc_pacote),0), 
		coalesce(max(ie_auditoria),'N') 
	into STRICT	nr_atendimento_w, 
		cd_convenio_w, 
		nr_interno_conta_w, 
		cd_procedimento_w, 
		nr_seq_proc_pacote_w, 
		ie_auditoria_w 
	from	Procedimento_paciente 
	where	nr_sequencia		= nr_sequencia_p;	
 
	if (nr_seq_proc_pacote_w = nr_sequencia_p) then 
		--r.aise_application_error(-20011, 'O procedimento do pacote não pode ser retirado!#@#@'); 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263432);
	end if;	
	 
 
	update	Procedimento_paciente 
	set	nr_seq_proc_pacote	 = NULL, 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp(), 
		ie_auditoria       = 'N' 
	where 	nr_sequencia		= nr_sequencia_p 
	and	(nr_seq_proc_pacote IS NOT NULL AND nr_seq_proc_pacote::text <> '');
 
	--Gerar_Log_Pacote(nr_interno_conta_w, nr_seq_proc_pacote_w, 'Retirar procedimento pacote - ' || cd_procedimento_w || ' Seq. ' || nr_sequencia_p , nm_usuario_p,null,null); 
	CALL Gerar_Log_Pacote(nr_interno_conta_w, nr_seq_proc_pacote_w, WHEB_MENSAGEM_PCK.get_texto(298845, 'CD_PROCEDIMENTO=' || cd_procedimento_w || ';' || 'NR_SEQUENCIA=' || nr_sequencia_p), nm_usuario_p,null,null);
 
	CALL atualiza_preco_procedimento(nr_sequencia_p, cd_convenio_w, nm_usuario_p);
	 
	update	Procedimento_paciente 
	set	ie_auditoria       = ie_auditoria_w 
	where 	nr_sequencia		= nr_sequencia_p;
	 
else 
 
	select	coalesce(max(nr_atendimento),0), 
		coalesce(max(cd_convenio),0), 
		coalesce(max(nr_interno_conta),0), 
		coalesce(max(cd_material),0), 
		coalesce(max(nr_seq_proc_pacote),0), 
		coalesce(max(ie_auditoria),'N') 
	into STRICT	nr_atendimento_w, 
		cd_convenio_w, 
		nr_interno_conta_w, 
		cd_material_w, 
		nr_seq_proc_pacote_w, 
		ie_auditoria_w 
	from	material_atend_paciente 
	where	nr_sequencia		= nr_sequencia_p;	
 
 
	update	Material_atend_paciente 
	set	nr_seq_proc_pacote	 = NULL, 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp(), 
		ie_auditoria       = 'N' 
	where 	nr_sequencia		= nr_sequencia_p 
	and	(nr_seq_proc_pacote IS NOT NULL AND nr_seq_proc_pacote::text <> '');
 
	--Gerar_Log_Pacote(nr_interno_conta_w, nr_seq_proc_pacote_w, 'Retirar material pacote - ' || cd_material_w || ' Seq. ' || nr_sequencia_p , nm_usuario_p,null,null); 
	CALL Gerar_Log_Pacote(nr_interno_conta_w, nr_seq_proc_pacote_w, WHEB_MENSAGEM_PCK.get_texto(298846, 'CD_MATERIAL=' || cd_material_w || ';' || 'NR_SEQUENCIA=' || nr_sequencia_p) , nm_usuario_p,null,null);		
 
	CALL atualiza_preco_material(nr_sequencia_p, nm_usuario_p);
	 
	update	Material_atend_paciente 
	set	ie_auditoria       = ie_auditoria_w 
	where 	nr_sequencia		= nr_sequencia_p;
	 
end if;
 
if (nr_atendimento_w > 0) and (ie_ratear_pacote_p = 'S') then 
	CALL Ratear_Valores_Pacote(nr_atendimento_w, 'N', cd_convenio_w, nm_usuario_p);
 
	--Gerar_Log_Pacote(nr_interno_conta_w, nr_seq_proc_pacote_w, 'Ratear pacote após retirar item ' || nr_sequencia_p , nm_usuario_p,null,null); 
	CALL Gerar_Log_Pacote(nr_interno_conta_w, nr_seq_proc_pacote_w, WHEB_MENSAGEM_PCK.get_texto(298847, 'NR_SEQUENCIA=' || nr_sequencia_p) , nm_usuario_p,null,null);	
 
end if;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE retirar_item_pacote (nr_sequencia_p bigint, ie_proc_mat_p bigint, ie_ratear_pacote_p text, nm_usuario_p text) FROM PUBLIC;

