CREATE OR REPLACE PROCEDURE retirar_titulos_bordero_antigo ( ie_desp_bordero_p text, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_retirar_tit_cheque_w		varchar(1);


BEGIN

ie_retirar_tit_cheque_w	:= coalesce(obter_valor_param_usuario(855,58,obter_perfil_ativo,nm_usuario_p,wheb_usuario_pck.get_cd_estabelecimento),'N');

update	titulo_pagar
set	nr_bordero		 = NULL,
	vl_bordero		 = NULL,
	vl_juros_bordero	 = NULL,
	vl_multa_bordero	 = NULL,
	vl_desconto_bordero 	 = NULL
where 	nr_titulo = nr_titulo_p;


if (ie_desp_bordero_p = 'S') then

	update 	titulo_pagar
	set	vl_out_desp_bordero	 = NULL
	where	nr_titulo 		= nr_titulo_p;

end if;

if (ie_retirar_tit_cheque_w = 'S') then

	update	titulo_pagar
	set	nr_bordero		 = NULL,
		vl_bordero		 = NULL,
		vl_juros_bordero	 = NULL,
		vl_multa_bordero	 = NULL,
		vl_desconto_bordero 	 = NULL
	where 	substr(obter_cheque_titulo_pagar(nr_titulo),1,20) = substr(obter_cheque_titulo_pagar(nr_titulo_p),1,20);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
