SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE retornar_os_cliente_teste ( nr_seq_ordem_p bigint, nm_usuario_p text, ds_aviso_w INOUT text, ds_erro_w INOUT text) AS $body$
DECLARE


ie_teste_analista_w	varchar(1);
ie_resp_teste_w		man_ordem_servico.ie_resp_teste%type;
ie_cliente_teste_w	varchar(1);
ie_hist_nao_lib_w	varchar(1);
nm_user_w		varchar(15);
ie_usuario_sup_w 	varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_teste_analista_w
from	man_ordem_serv_tecnico
where	nr_seq_tipo = 31
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	nr_seq_ordem_serv = nr_seq_ordem_p;

select	max(ie_resp_teste)
into STRICT	ie_resp_teste_w
from	man_ordem_servico
where	nr_sequencia = nr_seq_ordem_p;

select	coalesce(max('S'),'N')
into STRICT	ie_cliente_teste_w
from	man_ordem_servico b,
	man_ordem_serv_estagio a
where	a.nr_seq_estagio =  CASE WHEN b.nr_grupo_trabalho=132 THEN  791  ELSE 2 END
and	a.nr_seq_ordem = b.nr_sequencia
and	a.nr_seq_ordem = nr_seq_ordem_p;

select	coalesce(max('S'),'N')
into STRICT	ie_usuario_sup_w
from	grupo_suporte b,
	usuario_grupo_sup c,
	gerencia_wheb g
where	b.nr_sequencia = c.nr_seq_grupo
and	c.nm_usuario_grupo = nm_usuario_p
and	g.nr_sequencia = b.nr_seq_gerencia_sup
and	g.ie_area_gerencia in ('SUP');


if	((ie_usuario_sup_w = 'S') or
	(((ie_resp_teste_w = 'A' and ie_teste_analista_w = 'S') or (ie_resp_teste_w <> 'A')) and
	  ie_cliente_teste_w = 'S')) then
	begin
		select	username
		into STRICT	nm_user_w
		from	USER_USERS;

		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_hist_nao_lib_w
		from	man_ordem_serv_tecnico
		where	nm_usuario = nm_usuario_p
		and	nr_seq_ordem_serv = nr_seq_ordem_p
		and coalesce(dt_liberacao::text, '') = '';

		if (	nm_user_w = 'CORP' and
			ie_hist_nao_lib_w = 'S') then
			ds_aviso_w := wheb_mensagem_pck.get_texto(318309); -- Existem históricos não liberados
		end if;

		update man_ordem_servico
		set    nr_seq_estagio = CASE WHEN nr_grupo_trabalho=132 THEN  791  ELSE 2 END ,
	           dt_versao_prev = to_date(obter_proxima_versao(clock_timestamp(), 0, 'A'), 'dd/mm/yyyy'),
	           nm_usuario     = nm_usuario_p,
	           dt_atualizacao = clock_timestamp()
	    where  nr_sequencia   = nr_seq_ordem_p;
	end;
	else
		ds_erro_w := wheb_mensagem_pck.get_texto(318310); -- A ordem de serviço não pode retornar para o estágio Cliente (Teste).
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE retornar_os_cliente_teste ( nr_seq_ordem_p bigint, nm_usuario_p text, ds_aviso_w INOUT text, ds_erro_w INOUT text) FROM PUBLIC;

