SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE retorna_cliente_teste ( nr_seq_ordem_servico_p bigint, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar a ordem de serviço para cliente teste caso já tenha estado neste estágio e possua teste do
analista
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_teste_analista_w		varchar(1);
ie_cliente_teste_w		varchar(1);
qt_historico_w			bigint;


BEGIN
select	coalesce(max('S'), 'N')
into STRICT	ie_teste_analista_w
from	man_ordem_serv_tecnico
where	nr_seq_tipo		= 31
and	nr_seq_ordem_serv	= nr_seq_ordem_servico_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

select	coalesce(max('S'), 'N')
into STRICT	ie_cliente_teste_w
from	man_ordem_servico	b,
	man_ordem_serv_estagio	a
where	a.nr_seq_estagio	= CASE WHEN b.nr_grupo_trabalho=132 THEN  791  ELSE 2 END
and	b.nr_sequencia		= a.nr_seq_ordem
and	a.nr_seq_ordem		= nr_seq_ordem_servico_p;

if (ie_teste_analista_w = 'S') and (ie_cliente_teste_w = 'S') then
	update	man_ordem_servico
	set	nr_seq_estagio	= CASE WHEN nr_grupo_trabalho=132 THEN  791  ELSE 2 END ,
		dt_versao_prev	= to_date(obter_proxima_versao(clock_timestamp(), 0, 'A'), 'dd/mm/yyyy'),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_ordem_servico_p;
end if;

select	count(*) qt_historico
into STRICT	qt_historico_w
from	man_ordem_serv_tecnico
where	nm_usuario		= nm_usuario_p
and	nr_seq_ordem_serv	= nr_seq_ordem_servico_p
and	coalesce(dt_liberacao::text, '') = '';

if (qt_historico_w > 0)then
	ds_mensagem_p := obter_texto_tasy(313175,wheb_usuario_pck.get_nr_seq_idioma);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE retorna_cliente_teste ( nr_seq_ordem_servico_p bigint, nm_usuario_p text, ds_mensagem_p INOUT text) FROM PUBLIC;

