CREATE OR REPLACE PROCEDURE retorno_prot_guia_cancel (nr_seq_protocolo_p bigint, nr_guia_prestador_p text, ie_status_cancelamento_p text, ds_codigo_glosa_erro_p text, ds_msg_glosa_erro_p text, ds_msg_glosa_erro_out_p INOUT text) AS $body$
DECLARE



	nm_usuario_w		varchar(15) := wheb_usuario_pck.get_nm_usuario;

BEGIN

if (coalesce(ds_codigo_glosa_erro_p::text, '') = '') and (coalesce(ds_msg_glosa_erro_p::text, '') = '') then
	update	tiss_prot_guia_cancel
	set	ie_status_cancelamento = ie_status_cancelamento_p,
		nm_usuario	= nm_usuario_w,
		dt_atualizacao 	= clock_timestamp(),
		ds_mensagem_erro  = NULL
	where	nr_seq_protocolo	= nr_seq_protocolo_p
	and	nr_guia_prestador	= nr_guia_prestador_p;
elsif (ds_codigo_glosa_erro_p IS NOT NULL AND ds_codigo_glosa_erro_p::text <> '') and (ds_msg_glosa_erro_p IS NOT NULL AND ds_msg_glosa_erro_p::text <> '') then
	update	tiss_prot_guia_cancel
	set	ds_mensagem_erro = obter_desc_expressao(627524) || ': ' || ds_codigo_glosa_erro_p || chr(13) || obter_desc_expressao(760580) || ': ' || ds_msg_glosa_erro_p,
		nm_usuario	= nm_usuario_w,
		dt_atualizacao 	= clock_timestamp()
	where	nr_seq_protocolo	= nr_seq_protocolo_p
	and	coalesce(ie_status_cancelamento::text, '') = ''
	returning ds_mensagem_erro into ds_msg_glosa_erro_out_p;
end if;

commit;

end;
