CREATE OR REPLACE PROCEDURE return_card_locations ( nm_usuario_p text, ie_type_p text, qt_x_p INOUT bigint, qt_y_p INOUT bigint, ds_title_p text) AS $body$
DECLARE

			
qt_x_w	safety_warnings_position.qt_position_x%type;
qt_y_w	safety_warnings_position.qt_position_y%type;		


BEGIN
	if (ie_type_p = 'AD') then
		begin
		select	qt_position_x,
			qt_position_y
		into STRICT	qt_x_w,
			qt_y_w
		from	safety_warnings_position
		where	nm_usuario = nm_usuario_p
		and	ie_type	= ie_type_p
		and	ds_title   = ds_title_p;
		exception
		when others then
			qt_x_w := 0;
			qt_y_w := 0;
		end;
		
	end if;
	
	if (ie_type_p <> 'AD') then
		begin
		select	qt_position_x,
			qt_position_y
		into STRICT	qt_x_w,
			qt_y_w
		from	safety_warnings_position
		where	nm_usuario = nm_usuario_p
		and	ie_type	= ie_type_p;
		exception
		when others then
			qt_x_w := 0;
			qt_y_w := 0;
		end;
	end if;
	
	
	if (qt_x_w >= 0  and qt_y_w >= 0) then
		qt_x_p := qt_x_w;
		qt_y_p := qt_y_w;
	end if;
	
end;
$body$
