CREATE OR REPLACE PROCEDURE reverter_adm_horario_solucao ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_p bigint, dt_instalacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE


ie_status_ant_w		varchar(5);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nr_etapa_p IS NOT NULL AND nr_etapa_p::text <> '') and (dt_instalacao_p IS NOT NULL AND dt_instalacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	ie_status_ant_w	:= obter_status_solucao_prescr(1, nr_prescricao_p, nr_seq_solucao_p);

	update	prescr_mat_hor
	set		dt_fim_horario  = NULL,
			nm_usuario_adm  = NULL,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			dt_interrupcao = CASE WHEN dt_reinicio = NULL THEN  null  ELSE CASE WHEN dt_inicio_horario = NULL THEN  null  ELSE clock_timestamp() END  END ,
			dt_reinicio = CASE WHEN dt_interrupcao = NULL THEN  null  ELSE CASE WHEN ie_status_ant_w='R' THEN  coalesce(dt_reinicio,clock_timestamp())  ELSE null END  END
	where	nr_etapa_sol = nr_etapa_p
	and		nr_seq_solucao = nr_seq_solucao_p
	and		nr_prescricao = nr_prescricao_p;
	end;
end if;
commit;
end;
$body$
