CREATE OR REPLACE PROCEDURE reverter_aprazamento_sol_proc ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_processo_w		bigint;
ie_status_processo_w	varchar(15);
dt_cancelamento_w		timestamp;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nr_etapa_p IS NOT NULL AND nr_etapa_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_processo_w
	from	adep_processo
	where	nr_prescricao	= nr_prescricao_p
	and		nr_seq_solucao	= nr_seq_solucao_p
	and		nr_etapa	= nr_etapa_p;

	if (nr_seq_processo_w > 0) then
		begin
		select	ie_status_processo,
				dt_cancelamento
		into STRICT	ie_status_processo_w,
				dt_cancelamento_w
		from	adep_processo
		where	nr_sequencia = nr_seq_processo_w;

		if (ie_status_processo_w not in ('G','D')) and (coalesce(dt_cancelamento_w::text, '') = '') then
			begin
			---Não é possível reverter o aprazamento desta etapa (#@NR_ETAPA#@).O processo correspondente à mesma (#@NR_SEQ_PROCESSO#@) já está sendo preparado, favor verificar!
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(261484,'NR_ETAPA='||to_char(nr_etapa_p)||';NR_SEQ_PROCESSO='||to_char(nr_seq_processo_w));
			end;
		elsif (coalesce(dt_cancelamento_w::text, '') = '') then
			begin
			update	adep_processo
			set		dt_cancelamento			= CASE WHEN coalesce(dt_cancelamento,null) = NULL THEN  clock_timestamp()  ELSE null END ,
					nm_usuario_cancelamento	= CASE WHEN coalesce(nm_usuario_cancelamento,null) = NULL THEN  nm_usuario_p  ELSE null END
			where	nr_sequencia			= nr_seq_processo_w;

			CALL atualizar_status_etapa_sol_pmh(nr_prescricao_p, nr_seq_solucao_p, nr_etapa_p, nm_usuario_p);
			end;
		end if;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
