CREATE OR REPLACE PROCEDURE reverter_inst_horario_solucao ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_p bigint, dt_instalacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE


ie_status_ant_w			varchar(5);
nr_etapa_atual_w		bigint;
ie_troca_frasco_w		varchar(1)	:= 'X';
nr_seq_evento_w			prescr_solucao_evento.nr_sequencia%type;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nr_etapa_p IS NOT NULL AND nr_etapa_p::text <> '') and (dt_instalacao_p IS NOT NULL AND dt_instalacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	nr_etapa_atual_w	:= Obter_etapa_atual(nr_prescricao_p, nr_seq_solucao_p);

	ie_status_ant_w	:= obter_status_solucao_prescr(1, nr_prescricao_p, nr_seq_solucao_p);

	if (ie_status_ant_w = 'II') then
		ie_status_ant_w	:=	'INT';
	end if;

	if (ie_status_ant_w in ('R', 'INT', 'II')) then
		select	coalesce(max(ie_status_ant_w),'N'),
				coalesce(max(nr_sequencia),0)
		into STRICT	ie_status_ant_w,
				nr_seq_evento_w
		from	prescr_solucao_evento
		where	coalesce(ie_evento_valido,'S') = 'S'
		and	ie_alteracao = 2
		and	nr_etapa_evento = nr_etapa_p
		and	nr_seq_solucao = nr_seq_solucao_p
		and	nr_prescricao = nr_prescricao_p;
	end if;

	if	(ie_status_ant_w = 'INT' AND nr_seq_evento_w > 0) then
		ie_troca_frasco_w	:= obter_se_evento_troca_frasco(nr_seq_evento_w);
	end if;

	update	prescr_mat_hor
	set	dt_inicio_horario = CASE WHEN ie_troca_frasco_w='N' THEN  clock_timestamp()  ELSE CASE WHEN coalesce(dt_interrupcao, dt_reinicio) = NULL THEN  null  ELSE CASE WHEN ie_status_ant_w='N' THEN  null  ELSE dt_inicio_horario END  END  END ,
		nm_usuario_inicio = CASE WHEN ie_status_ant_w='N' THEN  null  ELSE null END ,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_reinicio = CASE WHEN dt_interrupcao = NULL THEN  null  ELSE CASE WHEN ie_status_ant_w='R' THEN  clock_timestamp()  ELSE null END  END ,
		dt_interrupcao = CASE WHEN ie_troca_frasco_w='N' THEN  clock_timestamp()  ELSE CASE WHEN dt_reinicio = NULL THEN  null  ELSE CASE WHEN ie_status_ant_w='INT' THEN  clock_timestamp()  ELSE null END  END  END ,
		dt_recusa = CASE WHEN ie_status_ant_w='N' THEN  null  ELSE dt_recusa END ,
		dt_fim_horario = CASE WHEN ie_troca_frasco_w='S' THEN  null  ELSE dt_fim_horario END
	where	nr_etapa_sol = nr_etapa_p
	and	nr_seq_solucao = nr_seq_solucao_p
	and	nr_prescricao = nr_prescricao_p;

	if (nr_etapa_atual_w <> nr_etapa_p) then

		update	prescr_mat_hor
		set	dt_fim_horario  = NULL,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p/*,
			dt_reinicio = decode(dt_interrupcao, null, null, decode(ie_status_ant_w, 'R', nvl(dt_reinicio,sysdate), null)),
			dt_interrupcao = decode(dt_reinicio, null, null, decode(ie_status_ant_w, 'INT', nvl(dt_interrupcao,sysdate), null))*/
		where	nr_etapa_sol = nr_etapa_atual_w
		and	nr_seq_solucao = nr_seq_solucao_p
		and	nr_prescricao = nr_prescricao_p;
	end if;

	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
