SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reverter_reserva_horario (qt_minutos_p bigint) AS $body$
BEGIN

update	agenda_paciente
set	ie_status_agenda	= 'L',
	nm_usuario		= 'Tasy',
	nm_usuario_reserva	 = NULL,
	dt_reserva		 = NULL
where	dt_agendamento between trunc(clock_timestamp()) and clock_timestamp() + interval '86399 days'/86400
and	dt_reserva + qt_minutos_p/1440 < clock_timestamp()
and	(dt_reserva IS NOT NULL AND dt_reserva::text <> '')
and	ie_status_agenda = 'R';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
