CREATE OR REPLACE PROCEDURE revisar_info_adic_diagnostico (nr_seq_info_adic_orig_p bigint, nr_seq_info_adic_copy_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_diag_doenca_inf_adic_w 	diag_doenca_inf_adic.nr_seq_diag_doenca_inf_adic%type;

ie_acao_copiar_w             constant varchar(1) := 'C';
ie_acao_liberar_w            constant varchar(1) := 'L';
ie_acao_excluir_w            constant varchar(1) := 'E';


BEGIN

if (ie_acao_p = ie_acao_copiar_w) then

	update 	diag_doenca_inf_adic
	set 	nr_seq_diag_doenca_inf_adic = nr_seq_info_adic_orig_p,
			dt_liberacao  = NULL,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_info_adic_copy_p;

	update 	diag_doenca_inf_adic
	set 	ie_revisado = 'N'
	where	nr_sequencia = nr_seq_info_adic_orig_p;

elsif (ie_acao_p = ie_acao_liberar_w) then

	begin
		select 	nr_seq_diag_doenca_inf_adic
		into STRICT 	nr_seq_diag_doenca_inf_adic_w
		from 	diag_doenca_inf_adic
		where 	nr_sequencia = nr_seq_info_adic_orig_p;

		update 	diag_doenca_inf_adic
		set 	ie_revisado = 'S',
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_diag_doenca_inf_adic_w;


		if (nr_seq_diag_doenca_inf_adic_w IS NOT NULL AND nr_seq_diag_doenca_inf_adic_w::text <> '')then
			update 	diag_doenca_inf_adic
			set 	ie_revisado = 'R',
					nm_usuario = nm_usuario_p,
					dt_atualizacao = clock_timestamp()
			where	nr_sequencia = nr_seq_info_adic_orig_p;
		end if;

	exception
	when others then
		nr_seq_diag_doenca_inf_adic_w := null;
	end;

else
	begin
		select 	nr_seq_diag_doenca_inf_adic
		into STRICT 	nr_seq_diag_doenca_inf_adic_w
		from 	diag_doenca_inf_adic
		where 	nr_sequencia = nr_seq_info_adic_orig_p;

		update 	diag_doenca_inf_adic
		set 	ie_revisado  = NULL,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_diag_doenca_inf_adic_w;

