CREATE OR REPLACE PROCEDURE rop_adic_barras_roupa ( nr_seq_lote_roupa_p bigint, qt_pecas_p bigint, cd_empresa_p bigint, nm_usuario_p text) AS $body$
DECLARE


i			bigint;
cd_barra_roupa_w		varchar(40);
ds_erro_w		varchar(255);
cd_empresa_w		empresa.cd_empresa%type;


BEGIN

cd_empresa_w	:= coalesce(cd_empresa_p,obter_empresa_estab(wheb_usuario_pck.get_cd_estabelecimento));

select	coalesce(max((somente_numero(cd_barra_roupa))::numeric ), 0)
into STRICT	cd_barra_roupa_w
from	rop_roupa;

for i in  1 .. qt_pecas_p loop

	cd_barra_roupa_w := (cd_barra_roupa_w)::numeric  + 1;

	ds_erro_w := rop_gerar_roupa(nr_seq_lote_roupa_p, cd_barra_roupa_w, cd_empresa_w, nm_usuario_p, ds_erro_w);

end loop;

commit;

end;
