CREATE OR REPLACE PROCEDURE rop_ajustar_tipo_lavanderia () AS $body$
DECLARE


qt_registro_w		bigint;
nr_sequencia_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	rop_tipo_operacao_lav
where	ie_evento = 'E';

if (qt_registro_w = 0) then

	select	nextval('rop_tipo_operacao_lav_seq')
	into STRICT	nr_sequencia_w
	;

	insert into rop_tipo_operacao_lav(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_operacao,
		ie_evento,
		ie_situacao)
	values (	nr_sequencia_w,
		clock_timestamp(),
		wheb_mensagem_pck.get_texto(799535),--'Tasy',
		clock_timestamp(),
		wheb_mensagem_pck.get_texto(799535),--'Tasy',
		wheb_mensagem_pck.get_texto(799536),--'Entrada',
		'E',
		'A');

	update	rop_lavanderia
	set	nr_seq_operacao = nr_sequencia_w
	where	ie_evento = 'E'
	and	coalesce(nr_seq_operacao::text, '') = '';
end if;

select	count(*)
into STRICT	qt_registro_w
from	rop_tipo_operacao_lav
where	ie_evento = 'S';

if (qt_registro_w = 0) then

	select	nextval('rop_tipo_operacao_lav_seq')
	into STRICT	nr_sequencia_w
	;

	insert into rop_tipo_operacao_lav(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_operacao,
		ie_evento,
		ie_situacao)
	values (	nr_sequencia_w,
		clock_timestamp(),
		wheb_mensagem_pck.get_texto(799535),--'Tasy',
		clock_timestamp(),
		wheb_mensagem_pck.get_texto(799535),--'Tasy',
		wheb_mensagem_pck.get_texto(799537),--'Saída',
		'S',
		'A');

	update	rop_lavanderia
	set	nr_seq_operacao = nr_sequencia_w
	where	ie_evento = 'S'
	and	coalesce(nr_seq_operacao::text, '') = '';
end if;


select	count(*)
into STRICT	qt_registro_w
from	rop_tipo_operacao_lav
where	ie_evento = 'D';

if (qt_registro_w = 0) then

	select	nextval('rop_tipo_operacao_lav_seq')
	into STRICT	nr_sequencia_w
	;

	insert into rop_tipo_operacao_lav(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_operacao,
		ie_evento,
		ie_situacao)
	values (	nr_sequencia_w,
		clock_timestamp(),
		wheb_mensagem_pck.get_texto(799535),--'Tasy',
		clock_timestamp(),
		wheb_mensagem_pck.get_texto(799535),--'Tasy',
		wheb_mensagem_pck.get_texto(799538),--'Entrada com Divergência',
		'D',
		'A');

	update	rop_lavanderia
	set	nr_seq_operacao = nr_sequencia_w
	where	ie_evento = 'D'
	and	coalesce(nr_seq_operacao::text, '') = '';
end if;

commit;

end;
$body$
