SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rop_baixar_pecas_sem_movto ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
cd_motivo_baixa_w			bigint;
qt_dias_w			bigint;
cd_empresa_w			bigint;

c01 CURSOR FOR
SELECT	b.nr_sequencia
from	rop_lote_roupa a,
	rop_roupa b
where	a.nr_sequencia = b.nr_seq_lote_roupa
and	a.cd_estabelecimento = cd_estabelecimento_p
and	b.cd_empresa = cd_empresa_w
and	coalesce(b.dt_baixa::text, '') = ''
and	not exists (
	SELECT  1
	from   	rop_movto_roupa c
	where	c.nr_seq_roupa = b.nr_sequencia
	and 	trunc(dt_origem) between  trunc(clock_timestamp()) - qt_dias_w  and trunc(clock_timestamp()));


BEGIN

select	cd_empresa
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_p;

select	coalesce(max(qt_dias_baixa_peca),0),
	coalesce(max(cd_motivo_baixa),0)
into STRICT	qt_dias_w,
	cd_motivo_baixa_w
from	rop_parametros_rouparia
where	cd_estabelecimento = cd_estabelecimento_p;

if (qt_dias_w > 0) and (cd_motivo_baixa_w > 0) then

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	rop_roupa
		set	cd_motivo_baixa	= cd_motivo_baixa_w,
			ie_situacao	= 'I',
			dt_baixa		= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_w;

		insert into rop_roupa_hist(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_roupa,
			dt_historico,
			ds_titulo,
			ds_historico,
			ie_tipo,
			dt_liberacao,
			nm_usuario_lib,
			cd_motivo_baixa)
		values (	nextval('rop_roupa_hist_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_sequencia_w,
			clock_timestamp(),
			--'Baixa automática',
			wheb_mensagem_pck.get_texto(312260),
			--'Esta peça foi baixada automaticamente através da JOB - ROP_BAIXAR_PECAS_SEM_MOVTO',
			wheb_mensagem_pck.get_texto(312261),
			'S',
			clock_timestamp(),
			nm_usuario_p,
			cd_motivo_baixa_w);

		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
