CREATE OR REPLACE PROCEDURE rop_carrega_saldo_roupa ( dt_mesano_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_mes_base_w		timestamp;
dt_novo_mes_w		timestamp;
nr_seq_roupa_w		bigint;
nr_seq_local_w		bigint;
qt_saldo_w		double precision;

/*Essa procedure não é chamada em nenhum lugar. Ela foi criada apenas para rodar nos clientes que por ventura não tem a JOB ROP_VIRADA_SALDO_ROUPA, e precisam copiar o saldo do mes anterior para o próximo mês*/

c01 CURSOR FOR
SELECT	a.nr_seq_roupa,
	a.nr_seq_local,
	a.qt_saldo
from	rop_saldo_roupa a
where	a.dt_mesano_referencia = dt_mes_base_w
and not exists (
	SELECT	1
	from	rop_saldo_roupa x
	where	x.nr_seq_roupa = a.nr_seq_roupa
	and	x.nr_seq_local = a.nr_seq_local
	and	x.dt_mesano_referencia = dt_novo_mes_w);


BEGIN

dt_mes_base_w	:= PKG_DATE_UTILS.start_of(PKG_DATE_UTILS.ADD_MONTH(dt_mesano_referencia_p,-1,0),'month',0);
dt_novo_mes_w	:= PKG_DATE_UTILS.start_of(dt_mesano_referencia_p,'month',0);

--if	(PKG_DATE_UTILS.start_of(sysdate,'dd',0) = PKG_DATE_UTILS.start_of(sysdate,'mm',0)) then
	begin

	open C01;
	loop
	fetch C01 into
		nr_seq_roupa_w,
		nr_seq_local_w,
		qt_saldo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		insert into rop_saldo_roupa(
			nr_seq_roupa,
			nr_seq_local,
			dt_mesano_referencia,
			qt_saldo)
		values (	nr_seq_roupa_w,
			nr_seq_local_w,
			dt_novo_mes_w,
			qt_saldo_w);
		end;
	end loop;
	close C01;
	end;
--end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
