SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rop_conferir_pecas ( nr_seq_lote_p bigint, cd_barra_roupa_p text, ie_saida_chegada_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_roupa_w			bigint;
qt_existe_w			bigint;
ds_erro_w			varchar(255);


BEGIN

begin
select	a.nr_sequencia
into STRICT	nr_seq_roupa_w
from	rop_lote_roupa b,
	rop_roupa a
where	a.nr_seq_lote_roupa	= b.nr_sequencia
and	a.cd_barra_roupa		= cd_barra_roupa_p
and	a.ie_situacao		= 'A';
exception
	when no_data_found then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265534,'CD_BARRA_ROUPA=' || cd_barra_roupa_p);
		--'Esta peça não existe no cadastro da roupa - Cód.Barras: ' || cd_barra_roupa_p || '.'
end;

if (ie_saida_chegada_p = 'S') then
	update	rop_movto_roupa
	set	dt_conf_saida		= clock_timestamp(),
		nm_usuario_conf_saida	= nm_usuario_p
	where	nr_seq_lote		= nr_seq_lote_p
	and	nr_seq_roupa		= nr_seq_roupa_w;
elsif (ie_saida_chegada_p = 'C') then
	update	rop_movto_roupa
	set	dt_conf_chegada		= clock_timestamp(),
		nm_usuario_conf_chegada	= nm_usuario_p
	where	nr_seq_lote		= nr_seq_lote_p
	and	nr_seq_roupa		= nr_seq_roupa_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
