CREATE OR REPLACE PROCEDURE rop_consiste_lote_movto_roupa ( nr_seq_lote_movto_p bigint, ds_erro INOUT text) AS $body$
DECLARE


ds_erro_w			varchar(255):= '';
nr_seq_operacao_w		bigint;
nr_seq_local_w		bigint;
nr_seq_motivo_baixa_w	bigint;
nr_seq_local_orig_dest_w	bigint;
dt_liberacao_w		timestamp;
ie_operacao_w			varchar(15);


BEGIN

select	nr_seq_operacao,
	nr_seq_local,
	nr_seq_motivo_baixa,
	nr_seq_local_orig_dest,
	dt_liberacao
into STRICT	nr_seq_operacao_w,
	nr_seq_local_w,
	nr_seq_motivo_baixa_w,
	nr_seq_local_orig_dest_w,
	dt_liberacao_w
from	rop_lote_movto
where	nr_sequencia = nr_seq_lote_movto_p;

select	ie_operacao
into STRICT	ie_operacao_w
from	rop_operacao
where	nr_sequencia = nr_seq_operacao_w;

if (coalesce(dt_liberacao_w::text, '') = '') then
	if (ie_operacao_w = 'TS') and (coalesce(nr_seq_local_orig_dest_w,0) = 0) then
		ds_erro_w := ds_erro_w || '* Operação de Transferência deve ter o local destino informado !' || chr(13) || chr(10);
	elsif (ie_operacao_w = 'TS') and (nr_seq_local_orig_dest_w = nr_seq_local_w) then
		ds_erro_w := ds_erro_w || '* Operação de Transferência, o local de orig/dest deve ser diferente do local !' || chr(13) || chr(10);
	elsif (ie_operacao_w = 'B') and (coalesce(nr_seq_motivo_baixa_w,0) = 0) then
		ds_erro_w := ds_erro_w || '* Operação de Baixa deve ter o motivo de baixa informado !' || chr(13) || chr(10);
	end if;
end if;

ds_erro := ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
