CREATE OR REPLACE PROCEDURE rop_consiste_roupa_lavanderia ( nr_seq_lavanderia_p bigint, cd_barra_roupa_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


ie_evento_w			varchar(15);
cd_estabelecimento_w		smallint;
ie_consiste_local_saida_w		varchar(1);
ie_consiste_local_entrada_w		varchar(1);
nr_seq_roupa_w			bigint;
nr_seq_roupa_ww			bigint;
ds_erro_w			varchar(255);


BEGIN

begin
select	a.nr_sequencia
into STRICT	nr_seq_roupa_ww
from	rop_lote_roupa b,
	rop_roupa a
where	a.nr_seq_lote_roupa	= b.nr_sequencia
and	a.cd_barra_roupa	= cd_barra_roupa_p
and	a.ie_situacao		= 'A';

exception
	when no_data_found then
		ds_erro_w := substr(ds_erro_w || WHEB_MENSAGEM_PCK.get_texto(279995) || cd_barra_roupa_p || chr(13) || chr(10),1,255);
end;

select	a.ie_evento,
	a.cd_estabelecimento
into STRICT	ie_evento_w,
	cd_estabelecimento_w
from	rop_lavanderia a
where	a.nr_sequencia = nr_seq_lavanderia_p;

select	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_seq_roupa_w
from	rop_roupa a
where	a.cd_barra_roupa = cd_barra_roupa_p;

select	substr(coalesce(obter_valor_param_usuario(1301, 11, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w),'N'),1,1),
	substr(coalesce(obter_valor_param_usuario(1301, 12, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w),'N'),1,1)
into STRICT	ie_consiste_local_saida_w,
	ie_consiste_local_entrada_w
;


if (nr_seq_roupa_w > 0) then

	if (ie_evento_w = 'S') and (ie_consiste_local_saida_w = 'S') and (rop_obter_se_peca_lav_interna(nr_seq_roupa_w) = 'N') then
		ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279996);
	end if;

	if (ie_evento_w = 'E') and (ie_consiste_local_entrada_w = 'S') and (rop_obter_se_peca_lav_externa(nr_seq_roupa_w) = 'N') then
		ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279997);
	end if;
else
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279998);
end if;

ds_erro_p		:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
