CREATE OR REPLACE PROCEDURE rop_consiste_todas_pecas ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_roupa_w		bigint;
ds_erro_w			varchar(255);
ds_mensagem_w			varchar(255);

c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_seq_roupa
from	rop_movto_roupa
where	nr_seq_lote = nr_seq_lote_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	nr_seq_roupa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_erro_w := Rop_Consiste_item_Movto_Roupa(
		nr_sequencia_w, nr_seq_roupa_w, nm_usuario_p, cd_estabelecimento_p, ds_erro_w);
	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
		ds_mensagem_w := substr(ds_mensagem_w || ds_erro_w,1,255);
	end if;
	end;
end loop;
close c01;

ds_erro_p := ds_mensagem_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
