SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rop_consitir_conf_pecas ( nr_seq_lote_p bigint, cd_barra_roupa_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_roupa_w			bigint;
qt_existe_w			bigint;
ds_erro_w			varchar(255);


BEGIN

begin
select	a.nr_sequencia
into STRICT	nr_seq_roupa_w
from	rop_lote_roupa b,
	rop_roupa a
where	a.nr_seq_lote_roupa	= b.nr_sequencia
and	a.cd_barra_roupa		= cd_barra_roupa_p
and	a.ie_situacao		= 'A';
exception
	when no_data_found then
		ds_erro_w := substr(ds_erro_w || WHEB_MENSAGEM_PCK.get_texto(281445) || cd_barra_roupa_p || chr(13) || chr(10),1,255);
end;

if (coalesce(ds_erro_w::text, '') = '') then

	select	count(*)
	into STRICT	qt_existe_w
	from	rop_movto_roupa
	where	nr_seq_lote = nr_seq_lote_p
	and	nr_seq_roupa = nr_seq_roupa_w;

	if (qt_existe_w = 0) then
		ds_erro_w := substr(ds_erro_w || WHEB_MENSAGEM_PCK.get_texto(281446) || chr(13) || chr(10),1,255);
	end if;
end if;

ds_erro_p := ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
