SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rop_definir_peso_kit ( nr_seq_kit_rop_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_roupa_w	bigint;
qt_roupa_w	double precision;

/*Retorna peso do KIT*/

qt_peso_w	double precision := 0;

C01 CURSOR FOR
	SELECT	nr_seq_roupa,
		qt_roupa
	from	rop_componente_kit
	where	nr_seq_kit = nr_seq_kit_rop_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_roupa_w,
	qt_roupa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	qt_peso_w := (qt_peso_w + (coalesce(rop_obter_peso_roupa(nr_seq_roupa_w),0) * qt_roupa_w));

	end;
end loop;
close C01;

if (qt_peso_w >= 0) then
	begin

	update	rop_kit_rouparia
	set	qt_peso 	= qt_peso_w
	where	nr_sequencia	= nr_seq_kit_rop_p;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
