CREATE OR REPLACE PROCEDURE rop_fechar_inv_reposicao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN

update	rop_inv_reposicao
set	dt_fechamento		= clock_timestamp(),
	nm_usuario_fecham		= nm_usuario_p
where	nr_sequencia		= nr_sequencia_p;

update	ROP_INV_REPOSICAO_ITEM
set	qt_atendido = qt_repor
where	nr_seq_inventario = nr_sequencia_p
and	coalesce(qt_atendido::text, '') = '';



commit;

end;
$body$
