CREATE OR REPLACE PROCEDURE rop_final_contagem_inv_ajuste ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	rop_inv_ajuste_roupa
where	nr_seq_inv_setor = nr_sequencia_p;

if (qt_existe_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265535);
	--'Não foi lançado nenhum item para contagem neste setor.'
end if;

update	rop_inv_ajuste_setor
set	dt_final_contagem		= clock_timestamp(),
	nm_usuario_fim_cont	= nm_usuario_p
where	nr_sequencia		= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
