CREATE OR REPLACE PROCEDURE rop_gerar_itens_lavand_saida ( nr_seq_lavand_orig_p bigint, nr_seq_lavand_dest_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;
vl_preco_w		rop_lavanderia_item.vl_unitario%type;


BEGIN

if (nr_seq_lavand_orig_p IS NOT NULL AND nr_seq_lavand_orig_p::text <> '') and (nr_seq_lavand_dest_p IS NOT NULL AND nr_seq_lavand_dest_p::text <> '') then

	select	count(*)
	into STRICT	qt_registros_w
	from	rop_lavanderia_item
	where	nr_seq_lavanderia = nr_seq_lavand_dest_p;

	if	qt_registros_w = 0 then
		insert into rop_lavanderia_item(
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_kit,
			nr_seq_lavanderia,
			nr_seq_roupa,
			nr_sequencia,
			qt_peso,
			qt_roupa,
			vl_unitario)
		SELECT	clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			nr_seq_kit,
			nr_seq_lavand_dest_p,
			nr_seq_roupa,
			nextval('rop_lavanderia_item_seq'),
			qt_peso,
			qt_roupa,
			obter_vl_unit_rop_preco(nr_seq_roupa, clock_timestamp())
		from	rop_lavanderia_item
		where	nr_seq_lavanderia = nr_seq_lavand_orig_p;

		commit;



	end if;
end if;

end;
$body$
