CREATE OR REPLACE PROCEDURE rop_gerar_lote_movto_auto ( nr_seq_operacao_p bigint, nr_seq_local_dest_p bigint, ds_lista_lote_p INOUT text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(10);
nr_seq_local_atual_w	bigint;
cd_barra_roupa_w		varchar(40);
nr_seq_lote_movto_w	bigint;
nr_seq_movto_roupa_w	bigint;
nr_seq_roupa_w		bigint;
ds_lista_lote_w		varchar(255);
qt_roupa_w		double precision;

c01 CURSOR FOR 
	SELECT	distinct coalesce(rop_obter_local_roupa(cd_barra_roupa),0) 
	from	w_rop_lote_movto 
	where	nm_usuario = nm_usuario_p 
	order by 1;

c02 CURSOR FOR 
	SELECT	distinct cd_barra_roupa, 
		qt_roupa 
	from	w_rop_lote_movto 
	where	rop_obter_local_roupa(cd_barra_roupa) = nr_seq_local_atual_w 
	and	nm_usuario = nm_usuario_p;


BEGIN 
 
if (coalesce(nr_seq_local_dest_p,0) = 0) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265536);
	--'NÃ£o foi informado o local de estoque destino.' 
end if;
 
select	substr(obter_pessoa_fisica_usuario(nm_usuario_p,'C'),1,10) 
into STRICT	cd_pessoa_fisica_w
;
 
open c01;
loop 
fetch c01 into 
	nr_seq_local_atual_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	if (nr_seq_local_atual_w > 0) then 
		 
		select	nextval('rop_lote_movto_seq') 
		into STRICT	nr_seq_lote_movto_w 
		;
 
		insert into rop_lote_movto( 
			nr_sequencia, 
			cd_estabelecimento, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_seq_operacao, 
			cd_pessoa_fisica, 
			nr_seq_local, 
			nr_seq_local_orig_dest, 
			dt_registro) 
		values (nr_seq_lote_movto_w, 
			cd_estabelecimento_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_operacao_p, 
			cd_pessoa_fisica_w, 
			nr_seq_local_atual_w, 
			nr_seq_local_dest_p, 
			clock_timestamp());
 
		ds_lista_lote_w	:= nr_seq_lote_movto_w || ', ' || ds_lista_lote_w;
 
		open c02;
		loop 
		fetch c02 into 
			cd_barra_roupa_w, 
			qt_roupa_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_roupa_w 
			from	rop_roupa 
			where	cd_barra_roupa	= cd_barra_roupa_w 
			and	ie_situacao	= 'A';
 
			select	nextval('rop_movto_roupa_seq') 
			into STRICT	nr_seq_movto_roupa_w 
			;
 
			insert into rop_movto_roupa( 
				nr_sequencia, 
				nr_seq_lote, 
				dt_atualizacao, 
				nm_usuario, 
				dt_atualizacao_nrec, 
				nm_usuario_nrec, 
				nr_seq_roupa, 
				dt_origem, 
				cd_estabelecimento, 
				ie_oper_correta, 
				qt_roupa) 
			values (	nr_seq_movto_roupa_w, 
				nr_seq_lote_movto_w, 
				clock_timestamp(), 
				'Tasy_Auto', 
				clock_timestamp(), 
				nm_usuario_p, 
				nr_seq_roupa_w, 
				clock_timestamp(), 
				cd_estabelecimento_p, 
				'S', 
				qt_roupa_w);
			end;
		end loop;
		close c02;
	end if;
	end;	
end loop;
close c01;
 
select	substr(ds_lista_lote_w,1,length(ds_lista_lote_w)-2) 
into STRICT	ds_lista_lote_p
;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
