CREATE OR REPLACE PROCEDURE rop_gerar_lote_movto_roupa ( nr_sequencia_p bigint, cd_barras_p text, qt_roupa_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro INOUT text) AS $body$
DECLARE


cd_estabelecimento_w	smallint;
qt_existe_w		bigint;
qt_existe_movto_w		bigint;
nr_seq_roupa_w		bigint;
ie_oper_correta_w		varchar(1) := 'S';
nr_seq_movto_w		bigint;
ds_erro_w		varchar(255):= '';
ds_erro_item_w		varchar(255):= '';
qt_roupa_w		double precision;
cd_perfil_w		bigint;
ie_grava_quantidade_w	varchar(1);
ie_desconsidera_zeros_w varchar(1);
cd_barras_w		rop_roupa.cd_barra_roupa%type;


BEGIN

qt_roupa_w 		:= qt_roupa_p;
cd_perfil_w		:= obter_perfil_ativo;


ie_grava_quantidade_w	:= coalesce((obter_valor_param_usuario(1301, 95, cd_perfil_w, nm_usuario_p, cd_estabelecimento_p)), 'N');

ie_desconsidera_zeros_w	:= coalesce((obter_valor_param_usuario(1301, 100, cd_perfil_w, nm_usuario_p, cd_estabelecimento_p)), 'N');

if (ie_grava_quantidade_w = 'N') then
	qt_roupa_w := null;
end if;

if (ie_desconsidera_zeros_w = 'S') then
	cd_barras_w := substr(to_char((cd_barras_p)::numeric ),1,40);
else
	cd_barras_w := substr(cd_barras_p,1,40);
end if;

select	count(*)
into STRICT	qt_existe_w
from	rop_roupa
where	cd_barra_roupa = cd_barras_w;

if (qt_existe_w = 0) then
	--ds_erro_w	:= substr(ds_erro_w || '* Não existe nenhuma peça registrada no cadastro da roupa com este cód.barras: ' || cd_barras_w || chr(13) || chr(10),1,255);
	ds_erro_w	:= substr(ds_erro_w || wheb_mensagem_pck.get_texto(311759, 'CD_BARRAS=' || cd_barras_w) || chr(13) || chr(10),1,255);
elsif (qt_existe_w = 1) then
	select	nr_sequencia
	into STRICT	nr_seq_roupa_w
	from	rop_roupa
	where	cd_barra_roupa = cd_barras_w;
elsif (qt_existe_w > 1) then
	--ds_erro_w	:= substr(ds_erro_w || '* Existe mais de uma peça com mesmo cód.barras: ' || cd_barras_w || chr(13) || chr(10),1,255);
	ds_erro_w	:= substr(ds_erro_w || wheb_mensagem_pck.get_texto(311760, 'CD_BARRAS=' || cd_barras_w) || chr(13) || chr(10),1,255);
end if;

if (nr_seq_roupa_w IS NOT NULL AND nr_seq_roupa_w::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_existe_movto_w
	from	rop_movto_roupa
	where	nr_seq_roupa = nr_seq_roupa_w
	and	nr_seq_lote  = nr_sequencia_p;
	if (qt_existe_movto_w > 0) then
		--ds_erro_w	:= substr(ds_erro_w || '* Está peça já encontra-se neste lote, cód.barras: ' || cd_barras_w || chr(13) || chr(10),1,255);
		ds_erro_w	:= substr(ds_erro_w || wheb_mensagem_pck.get_texto(311763, 'CD_BARRAS=' || cd_barras_w) || chr(13) || chr(10),1,255);
	else
		begin
		select	cd_estabelecimento
		into STRICT	cd_estabelecimento_w
		from	rop_lote_movto
		where	nr_sequencia = nr_sequencia_p;

		select	nextval('rop_movto_roupa_seq')
		into STRICT	nr_seq_movto_w
		;

		insert into rop_movto_roupa(
			nr_sequencia,
			nr_seq_lote,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_roupa,
			dt_origem,
			cd_estabelecimento,
			ie_oper_correta,
			qt_roupa)
		Values (	nr_seq_movto_w,
			nr_sequencia_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_roupa_w,
			clock_timestamp(),
			cd_estabelecimento_w,
			ie_oper_correta_w,
			qt_roupa_w);

		ds_erro_item_w := Rop_Consiste_item_Movto_Roupa(nr_seq_movto_w, nr_seq_roupa_w, nm_usuario_p, cd_estabelecimento_p, ds_erro_item_w);
		commit;
		end;
	end if;
	end;
end if;

ds_erro := substr(ds_erro_w || ds_erro_item_w,1,255);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
