CREATE OR REPLACE PROCEDURE rop_gerar_movto_lav_automatico ( nr_sequencia_p bigint, nr_seq_local_org_dest_p bigint, cd_setor_atendimento_p bigint, cd_cnpj_lav_p text, cd_motorista_p text, cd_responsavel_p text, ds_lacre_p text, qt_peso_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_lavanderia_w				bigint;
nr_seq_roupa_w					bigint;
nr_seq_operacao_w				bigint;
vl_preco_w					rop_lavanderia_item.vl_unitario%type;

c01 CURSOR FOR 
SELECT	nr_seq_roupa 
from	rop_movto_roupa 
where	nr_seq_lote = nr_sequencia_p;


BEGIN 
 
select	coalesce(min(nr_sequencia),0) 
into STRICT	nr_seq_operacao_w 
from	rop_tipo_operacao_lav 
where	ie_evento = 'S';
 
if (nr_seq_operacao_w = 0) then 
	CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(247497);
end if;
 
select	nextval('rop_lavanderia_seq') 
into STRICT	nr_seq_lavanderia_w
;
 
insert into rop_lavanderia( 
	nr_sequencia, 
	cd_estabelecimento, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	ie_evento, 
	cd_cnpj, 
	cd_pessoa_transporte, 
	ds_lacre, 
	qt_peso, 
	dt_liberacao, 
	cd_pessoa_resp, 
	cd_setor_atendimento, 
	dt_envio_receb, 
	nr_seq_operacao) 
values (	nr_seq_lavanderia_w, 
	cd_estabelecimento_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	'S', 
	cd_cnpj_lav_p, 
	cd_motorista_p, 
	ds_lacre_p, 
	qt_peso_p, 
	clock_timestamp(), 
	cd_responsavel_p, 
	CASE WHEN cd_setor_atendimento_p=0 THEN null  ELSE cd_setor_atendimento_p END , 
	clock_timestamp(), 
	nr_seq_operacao_w);
 
open C01;
loop 
fetch C01 into	 
	nr_seq_roupa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	obter_vl_unit_rop_preco(nr_seq_roupa_w, clock_timestamp()) 
	into STRICT	vl_preco_w 
	;
	 
	insert into rop_lavanderia_item( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_lavanderia, 
		nr_seq_roupa, 
		qt_peso, 
		vl_unitario) 
	values (nextval('rop_lavanderia_item_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_seq_lavanderia_w, 
		nr_seq_roupa_w, 
		0, 
		vl_preco_w);
		 
	end;
end loop;
close C01;
 
CALL rop_gerar_movimento_lavanderia(nr_seq_lavanderia_w,nr_seq_local_org_dest_p,nm_usuario_p);
 
commit;
 
end;
$body$
